"use strict";

var fs = require("fs"),
    env = process.env.NODE_ENV || "development";

/*
 * Toplevel config
 */
var config = {
  env: env
};

/*
 * Application
 */
var appConfig = readConfig("app");
config.app = {
  httpPort: appConfig.httpPort ||
    process.env.PORT ||
    (env === "production" ? 80 : 8080),
  sessionSecret: appConfig.sessionSecret ||
    process.env.SESSION_SECRET ||
    "omgsupersecretlol",
  passwordSecret: appConfig.passwordSecret ||
    process.env.PASSWORD_SECRET ||
    "omgsupersecretlol",
  peerServerOpts: appConfig.peerServerOpts || {port: 900, path: "/"},
  bans: appConfig.bans || []
};

