/*
 * Util
 */
function readConfig(name) {
  var path = __dirname + "/../../config/"+name+".json";
  return fs.existsSync(path) ? JSON.parse(fs.readFileSync(path))[env] : {};
}

/*
 * Export
 */
module.exports = config;
